import React from "react";
import { useState } from "react";

export default function TripExpenseTracker() {
  const [people, setPeople] = useState([]);
  const [expenses, setExpenses] = useState([]);
  const [personName, setPersonName] = useState("");
  const [payer, setPayer] = useState("");
  const [item, setItem] = useState("");
  const [amount, setAmount] = useState("");
  const [selectedUsers, setSelectedUsers] = useState([]);

  const addPerson = () => {
    if (personName.trim() === "" || people.includes(personName)) {
      alert("Enter a valid name.");
      return;
    }
    setPeople([...people, personName]);
    setPersonName("");
  };

  const toggleUserSelection = (user) => {
    setSelectedUsers((prev) =>
      prev.includes(user) ? prev.filter((u) => u !== user) : [...prev, user]
    );
  };

  const addExpense = () => {
    if (!payer || item.trim() === "" || isNaN(amount) || amount <= 0 || selectedUsers.length === 0) {
      alert("Enter valid details.");
      return;
    }
    setExpenses([...expenses, { payer, item, amount: parseFloat(amount), users: selectedUsers }]);
    setPayer("");
    setItem("");
    setAmount("");
    setSelectedUsers([]);
  };

  return (
    <div className="container p-4 mx-auto max-w-xl bg-gray-100 rounded-lg shadow-lg">
      <h1 className="text-2xl font-bold mb-4">Trip Expense Tracker</h1>

      {/* Add People */}
      <div className="mb-4">
        <h2 className="text-lg font-semibold">Add People</h2>
        <input
          type="text"
          className="border p-2 w-full rounded"
          placeholder="Enter Name"
          value={personName}
          onChange={(e) => setPersonName(e.target.value)}
        />
        <button className="bg-blue-500 text-white p-2 rounded w-full mt-2" onClick={addPerson}>
          Add Person
        </button>
        <ul className="list-disc mt-2">
          {people.map((name) => (
            <li key={name}>{name}</li>
          ))}
        </ul>
      </div>

      {/* Add Expense */}
      <div className="mb-4">
        <h2 className="text-lg font-semibold">Add Expense</h2>
        <select className="border p-2 w-full rounded" value={payer} onChange={(e) => setPayer(e.target.value)}>
          <option value="">Select Payer</option>
          {people.map((name) => (
            <option key={name} value={name}>{name}</option>
          ))}
        </select>
        <input
          type="text"
          className="border p-2 w-full rounded mt-2"
          placeholder="Enter Item"
          value={item}
          onChange={(e) => setItem(e.target.value)}
        />
        <input
          type="number"
          className="border p-2 w-full rounded mt-2"
          placeholder="Enter Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />
        <h3 className="text-md font-medium mt-2">Select Users Who Used This Item:</h3>
        <div className="flex flex-wrap gap-2">
          {people.map((name) => (
            <label key={name} className="flex items-center gap-2">
              <input
                type="checkbox"
                checked={selectedUsers.includes(name)}
                onChange={() => toggleUserSelection(name)}
              />
              {name}
            </label>
          ))}
        </div>
        <button className="bg-green-500 text-white p-2 rounded w-full mt-2" onClick={addExpense}>
          Add Expense
        </button>
      </div>

      {/* Expenses Table */}
      <h2 className="text-lg font-semibold">Expenses Table</h2>
      <table className="w-full border mt-2">
        <thead>
          <tr className="bg-blue-500 text-white">
            <th>Payer</th>
            <th>Item</th>
            <th>Amount</th>
            <th>Shared Among</th>
          </tr>
        </thead>
        <tbody>
          {expenses.map((exp, index) => (
            <tr key={index} className="border">
              <td>{exp.payer}</td>
              <td>{exp.item}</td>
              <td>â‚¹{exp.amount.toFixed(2)}</td>
              <td>{exp.users.join(", ")}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
