import React, { useState } from "react";
import "./App.css";

const App = () => {
  const [people, setPeople] = useState([]);
  const [expenses, setExpenses] = useState([]);
  const [payer, setPayer] = useState("");
  const [item, setItem] = useState("");
  const [amount, setAmount] = useState("");
  const [selectedUsers, setSelectedUsers] = useState([]);

  const addPerson = (name) => {
    if (name && !people.includes(name)) {
      setPeople([...people, name]);
    }
  };

  const toggleUserSelection = (user) => {
    setSelectedUsers((prev) =>
      prev.includes(user) ? prev.filter((u) => u !== user) : [...prev, user]
    );
  };

  const addExpense = () => {
    if (payer && item && amount && selectedUsers.length > 0) {
      setExpenses([
        ...expenses,
        { payer, item, amount: parseFloat(amount), users: [...selectedUsers] },
      ]);
      setPayer("");
      setItem("");
      setAmount("");
      setSelectedUsers([]);
    }
  };

  const calculateExpenses = () => {
    let expenseSummary = {};
    people.forEach((person) => {
      expenseSummary[person] = { paid: 0, shared: 0, exclusive: 0 };
    });

    expenses.forEach(({ payer, amount, users }) => {
      expenseSummary[payer].paid += amount;
      if (users.length > 1) {
        let share = amount / users.length;
        users.forEach((user) => {
          expenseSummary[user].shared += share;
        });
      } else {
        expenseSummary[users[0]].exclusive += amount;
      }
    });

    return Object.keys(expenseSummary).map((person) => {
      let totalShare = expenseSummary[person].shared + expenseSummary[person].exclusive;
      return {
        name: person,
        paid: expenseSummary[person].paid,
        commonShare: expenseSummary[person].shared,
        exclusiveShare: expenseSummary[person].exclusive,
        totalShare,
        netAmount: expenseSummary[person].paid - totalShare,
      };
    });
  };

  return (
    <div className="container">
      <h1 className="title">Trip Expense Tracker</h1>

      {/* Add People */}
      <div className="section">
        <h2>Add People</h2>
        <input
          type="text"
          placeholder="Enter Name"
          onKeyDown={(e) => e.key === "Enter" && addPerson(e.target.value)}
        />
        <button onClick={() => addPerson(prompt("Enter Name:"))}>Add Person</button>
        <ul>
          {people.map((person) => (
            <li key={person}>{person}</li>
          ))}
        </ul>
      </div>

      {/* Add Expense */}
      <div className="section">
        <h2>Add Expense</h2>
        <select value={payer} onChange={(e) => setPayer(e.target.value)}>
          <option value="">Select Payer</option>
          {people.map((person) => (
            <option key={person} value={person}>
              {person}
            </option>
          ))}
        </select>
        <input
          type="text"
          placeholder="Enter Item"
          value={item}
          onChange={(e) => setItem(e.target.value)}
        />
        <input
          type="number"
          placeholder="Enter Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
        />
        <div>
          <h3>Select Users Who Used This Item:</h3>
          {people.map((person) => (
            <label key={person}>
              <input
                type="checkbox"
                checked={selectedUsers.includes(person)}
                onChange={() => toggleUserSelection(person)}
              />
              {person}
            </label>
          ))}
        </div>
        <button onClick={addExpense}>Add Expense</button>
      </div>

      {/* Expenses Table */}
      <div className="section table-container">
        <h2>Expenses Table</h2>
        <table>
          <thead>
            <tr>
              <th>Payer</th>
              <th>Item</th>
              <th>Amount</th>
              <th>Shared Among</th>
            </tr>
          </thead>
          <tbody>
            {expenses.map((exp, index) => (
              <tr key={index}>
                <td>{exp.payer}</td>
                <td>{exp.item}</td>
                <td>₹{exp.amount.toFixed(2)}</td>
                <td>{exp.users.join(", ")}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Individual Expenses Table */}
      <div className="section table-container">
        <h2>Individual Expenses</h2>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Diya Gaya Paisa</th>
              <th>Common Items Share</th>
              <th>Exclusive Items Share</th>
              <th>Total Share</th>
              <th>Net Amount</th>
            </tr>
          </thead>
          <tbody>
            {calculateExpenses().map((exp, index) => (
              <tr key={index}>
                <td>{exp.name}</td>
                <td>₹{exp.paid.toFixed(2)}</td>
                <td>₹{exp.commonShare.toFixed(2)}</td>
                <td>₹{exp.exclusiveShare.toFixed(2)}</td>
                <td>₹{exp.totalShare.toFixed(2)}</td>
                <td style={{ color: exp.netAmount < 0 ? "red" : "green" }}>
                  ₹{exp.netAmount.toFixed(2)}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default App;
