import React, { useState } from "react";
import "./App.css";
import { motion } from "framer-motion";

function App() {
  const [people, setPeople] = useState([]);
  const [payer, setPayer] = useState("");
  const [item, setItem] = useState("");
  const [amount, setAmount] = useState("");
  const [expenses, setExpenses] = useState([]);
  const [selectedUsers, setSelectedUsers] = useState([]);

  const addPerson = () => {
    const name = prompt("Enter Name:");
    if (name && !people.includes(name)) {
      setPeople([...people, name]);
    }
  };

  const addExpense = () => {
    if (payer && item && amount && selectedUsers.length > 0) {
      setExpenses([...expenses, { payer, item, amount: parseFloat(amount), users: selectedUsers }]);
      setPayer("");
      setItem("");
      setAmount("");
      setSelectedUsers([]);
    }
  };

  return (
    <div className="container">
      <h1>Trip Expense Tracker</h1>

      {/* Add People */}
      <div className="section">
        <h2>Add People</h2>
        <button className="add-btn" onClick={addPerson}>Add Person</button>
      </div>

      {/* Trip Members List */}
      {people.length > 0 && (
        <motion.div className="members-list" initial={{ opacity: 0, y: -10 }} animate={{ opacity: 1, y: 0 }}>
          <h3>Trip Members List</h3>
          <ul>
            {people.map((person, index) => (
              <motion.li key={index} initial={{ x: -10, opacity: 0 }} animate={{ x: 0, opacity: 1 }}>{person}</motion.li>
            ))}
          </ul>
        </motion.div>
      )}

      {/* Add Expense */}
      <div className="section">
        <h2>Add Expense</h2>
        <select value={payer} onChange={(e) => setPayer(e.target.value)}>
          <option value="">Select Payer</option>
          {people.map((person, index) => (
            <option key={index} value={person}>{person}</option>
          ))}
        </select>
        <input type="text" placeholder="Enter Item" value={item} onChange={(e) => setItem(e.target.value)} />
        <input type="number" placeholder="Enter Amount" value={amount} onChange={(e) => setAmount(e.target.value)} />
      </div>

      {/* Select Users */}
      <div className="section">
        <h3>Select Users Who Used This Item:</h3>
        <div className="user-selection">
          {people.map((person, index) => (
            <label key={index} className="user-label">
              <input
                type="checkbox"
                checked={selectedUsers.includes(person)}
                onChange={() => {
                  setSelectedUsers(selectedUsers.includes(person)
                    ? selectedUsers.filter((p) => p !== person)
                    : [...selectedUsers, person]);
                }}
              />
              {person}
            </label>
          ))}
        </div>
        <button className="add-expense-btn" onClick={addExpense}>Add Expense</button>
      </div>

      {/* Expenses Table */}
      {expenses.length > 0 && (
        <div className="expenses-table">
          <h2>Expenses Table</h2>
          <table>
            <thead>
              <tr>
                <th>Payer</th>
                <th>Item</th>
                <th>Amount</th>
                <th>Shared Among</th>
              </tr>
            </thead>
            <tbody>
              {expenses.map((exp, index) => (
                <tr key={index}>
                  <td>{exp.payer}</td>
                  <td>{exp.item}</td>
                  <td>â‚¹{exp.amount.toFixed(2)}</td>
                  <td>{exp.users.join(", ")}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default App;
