import React, { useState } from "react";
import "./App.css";

function App() {
  const [people, setPeople] = useState([]);
  const [newPerson, setNewPerson] = useState("");
  const [expenses, setExpenses] = useState([]);
  const [payer, setPayer] = useState("");
  const [item, setItem] = useState("");
  const [amount, setAmount] = useState("");
  const [selectedUsers, setSelectedUsers] = useState([]);

  const addPerson = () => {
    if (newPerson.trim() && !people.includes(newPerson)) {
      setPeople([...people, newPerson]);
      setNewPerson("");
    }
  };

  const addExpense = () => {
    if (payer && item && amount && selectedUsers.length) {
      setExpenses([...expenses, { payer, item, amount: parseFloat(amount), users: selectedUsers }]);
      setPayer("");
      setItem("");
      setAmount("");
      setSelectedUsers([]);
    }
  };

  return (
    <div className="container">
      <h1>Trip Expense Tracker</h1>

      <h2>Add People</h2>
      <input 
        type="text" 
        value={newPerson} 
        onChange={(e) => setNewPerson(e.target.value)} 
        placeholder="Enter Name"
      />
      <button onClick={addPerson}>Add Person</button>

      {people.length > 0 && (
        <div>
          <h3>Trip Members List</h3>
          <table>
            <thead>
              <tr>
                <th>Name</th>
              </tr>
            </thead>
            <tbody>
              {people.map((person, index) => (
                <tr key={index}>
                  <td>{person}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      <h2>Add Expense</h2>
      <select value={payer} onChange={(e) => setPayer(e.target.value)}>
        <option value="">Select Payer</option>
        {people.map((person, index) => (
          <option key={index} value={person}>{person}</option>
        ))}
      </select>
      <input type="text" value={item} onChange={(e) => setItem(e.target.value)} placeholder="Enter Item" />
      <input type="number" value={amount} onChange={(e) => setAmount(e.target.value)} placeholder="Enter Amount" />

      <h3>Select Users Who Used This Item:</h3>
      {people.map((person, index) => (
        <label key={index}>
          <input 
            type="checkbox" 
            checked={selectedUsers.includes(person)}
            onChange={() => {
              setSelectedUsers((prev) =>
                prev.includes(person) ? prev.filter((p) => p !== person) : [...prev, person]
              );
            }}
          />
          {person}
        </label>
      ))}
      <button onClick={addExpense}>Add Expense</button>

      {expenses.length > 0 && (
        <div>
          <h2>Expenses Table</h2>
          <table>
            <thead>
              <tr>
                <th>Payer</th>
                <th>Item</th>
                <th>Amount</th>
                <th>Shared Among</th>
              </tr>
            </thead>
            <tbody>
              {expenses.map((expense, index) => (
                <tr key={index}>
                  <td>{expense.payer}</td>
                  <td>{expense.item}</td>
                  <td>â‚¹{expense.amount.toFixed(2)}</td>
                  <td>{expense.users.join(", ")}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default App;
