import React, { useState } from "react";
import { motion } from "framer-motion";
import "./App.css";

function App() {
  const [people, setPeople] = useState([]);
  const [payer, setPayer] = useState("");
  const [item, setItem] = useState("");
  const [amount, setAmount] = useState("");
  const [selectedUsers, setSelectedUsers] = useState([]);
  const [expenses, setExpenses] = useState([]);

  const addPerson = () => {
    const name = prompt("Enter Name:");
    if (name && !people.includes(name)) {
      setPeople([...people, name]);
    }
  };

  const toggleUserSelection = (name) => {
    setSelectedUsers((prev) =>
      prev.includes(name)
        ? prev.filter((user) => user !== name)
        : [...prev, name]
    );
  };

  const addExpense = () => {
    if (payer && item && amount && selectedUsers.length > 0) {
      setExpenses([
        ...expenses,
        { payer, item, amount: parseFloat(amount), sharedAmong: [...selectedUsers] },
      ]);
      setPayer("");
      setItem("");
      setAmount("");
      setSelectedUsers([]);
    }
  };

  return (
    <div className="container">
      <h1 className="title">Trip Expense Tracker</h1>

      <h2>Add People</h2>
      <button className="add-button" onClick={addPerson}>Add Person</button>
      <h3>Trip Members List</h3>
      <motion.ul initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
        {people.map((person, index) => (
          <motion.li key={index} initial={{ x: -50 }} animate={{ x: 0 }}>
            {person}
          </motion.li>
        ))}
      </motion.ul>

      <h2>Add Expense</h2>
      <div className="input-group">
        <select value={payer} onChange={(e) => setPayer(e.target.value)}>
          <option value="">Select Payer</option>
          {people.map((person, index) => (
            <option key={index} value={person}>{person}</option>
          ))}
        </select>
        <input type="text" placeholder="Enter Item" value={item} onChange={(e) => setItem(e.target.value)} />
        <input type="number" placeholder="Enter Amount" value={amount} onChange={(e) => setAmount(e.target.value)} />
      </div>

      <h3>Select Users Who Used This Item:</h3>
      <div className="users-selection">
        {people.map((person, index) => (
          <label key={index} className="user-label">
            <input
              type="checkbox"
              checked={selectedUsers.includes(person)}
              onChange={() => toggleUserSelection(person)}
            />
            {person}
          </label>
        ))}
      </div>
      <button className="add-button" onClick={addExpense}>Add Expense</button>

      <h2>Expenses Table</h2>
      <table className="expenses-table">
        <thead>
          <tr>
            <th>Payer</th>
            <th>Item</th>
            <th>Amount</th>
            <th>Shared Among</th>
          </tr>
        </thead>
        <tbody>
          {expenses.map((expense, index) => (
            <tr key={index}>
              <td>{expense.payer}</td>
              <td>{expense.item}</td>
              <td>â‚¹{expense.amount.toFixed(2)}</td>
              <td>{expense.sharedAmong.join(", ")}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
