import React, { useState } from "react";
import { motion } from "framer-motion";
import "./App.css";

function App() {
  const [people, setPeople] = useState([]);
  const [expenses, setExpenses] = useState([]);
  const [payer, setPayer] = useState("");
  const [item, setItem] = useState("");
  const [amount, setAmount] = useState("");
  const [selectedUsers, setSelectedUsers] = useState([]);

  const addPerson = () => {
    const name = prompt("Enter Name:");
    if (name && !people.includes(name)) {
      setPeople([...people, name]);
    }
  };

  const toggleUserSelection = (user) => {
    if (selectedUsers.includes(user)) {
      setSelectedUsers(selectedUsers.filter((u) => u !== user));
    } else {
      setSelectedUsers([...selectedUsers, user]);
    }
  };

  const addExpense = () => {
    if (!payer || !item || !amount || selectedUsers.length === 0) {
      alert("Please fill all fields and select users");
      return;
    }
    setExpenses([...expenses, { payer, item, amount: parseFloat(amount), sharedAmong: selectedUsers }]);
    setItem("");
    setAmount("");
    setSelectedUsers([]);
  };

  return (
    <div className="container">
      <h1>Trip Expense Tracker</h1>

      <h2>Add People</h2>
      <button onClick={addPerson} className="btn">Add Person</button>

      <h3>Trip Members List</h3>
      <motion.ul initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
        {people.map((person, index) => (
          <motion.li key={index} layout>{person}</motion.li>
        ))}
      </motion.ul>

      <h2>Add Expense</h2>
      <div className="expense-form">
        <select onChange={(e) => setPayer(e.target.value)}>
          <option>Select Payer</option>
          {people.map((person, index) => (
            <option key={index} value={person}>{person}</option>
          ))}
        </select>
        <input type="text" placeholder="Enter Item" value={item} onChange={(e) => setItem(e.target.value)} />
        <input type="number" placeholder="Enter Amount" value={amount} onChange={(e) => setAmount(e.target.value)} />
      </div>

      <h3>Select Users Who Used This Item:</h3>
      <div className="user-selection">
        {people.map((person, index) => (
          <label key={index} className="checkbox-label">
            <input type="checkbox" checked={selectedUsers.includes(person)} onChange={() => toggleUserSelection(person)} />
            {person}
          </label>
        ))}
      </div>
      <button onClick={addExpense} className="btn">Add Expense</button>

      <h2>Expenses Table</h2>
      <table className="styled-table">
        <thead>
          <tr>
            <th>Payer</th>
            <th>Item</th>
            <th>Amount</th>
            <th>Shared Among</th>
          </tr>
        </thead>
        <tbody>
          {expenses.map((expense, index) => (
            <tr key={index}>
              <td>{expense.payer}</td>
              <td>{expense.item}</td>
              <td>â‚¹{expense.amount.toFixed(2)}</td>
              <td>{expense.sharedAmong.join(", ")}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
